import _pt from "prop-types";
import React, { PureComponent } from 'react';
import { Text as RNText, StyleSheet, Animated } from 'react-native';
import _ from 'lodash';
import { asBaseComponent, forwardRef } from "../../commons/new";
import { Colors } from "../../style";

/**
 * @description: A wrapper for Text component with extra functionality like modifiers support
 * @extends: Text
 * @extendsLink: https://reactnative.dev/docs/text
 * @modifiers: margins, color, typography
 * @example: https://github.com/wix/react-native-ui-lib/blob/master/demo/src/screens/componentScreens/TextScreen.js
 * @image: https://github.com/wix/react-native-ui-lib/blob/master/demo/showcase/Text/Modifiers.png?raw=true, https://github.com/wix/react-native-ui-lib/blob/master/demo/showcase/Text/Transformation.png?raw=true, https://github.com/wix/react-native-ui-lib/blob/master/demo/showcase/Text/Highlights.png?raw=true
 */
class Text extends PureComponent {
  static propTypes = {
    /**
         * color of the text
         */
    color: _pt.string,

    /**
         * whether to center the text (using textAlign)
         */
    center: _pt.bool,

    /**
         * whether to change the text to uppercase
         */
    uppercase: _pt.bool,

    /**
         * Substring to highlight
         */
    highlightString: _pt.oneOfType([_pt.string, _pt.arrayOf(_pt.string)]),

    /**
         * Use Animated.Text as a container
         */
    animated: _pt.bool,
    textAlign: _pt.string
  };
  static displayName = 'Text';
  TextContainer = this.props.animated ? Animated.createAnimatedComponent(RNText) : RNText; // setNativeProps(nativeProps) {
  //   this._root.setNativeProps(nativeProps); // eslint-disable-line
  // }

  getPartsByHighlight(targetString = '', highlightString) {
    if (typeof highlightString === 'string') {
      if (_.isEmpty(highlightString.trim())) {
        return [{
          string: targetString,
          shouldHighlight: false
        }];
      }

      return this.getTextPartsByHighlight(targetString, highlightString);
    } else {
      return this.getArrayPartsByHighlight(targetString, highlightString);
    }
  }

  getTextPartsByHighlight(targetString = '', highlightString = '') {
    if (highlightString === '') {
      return [{
        string: targetString,
        shouldHighlight: false
      }];
    }

    const textParts = [];
    let highlightIndex;

    do {
      highlightIndex = targetString.toLowerCase().indexOf(highlightString.toLowerCase());

      if (highlightIndex !== -1) {
        if (highlightIndex > 0) {
          textParts.push({
            string: targetString.substring(0, highlightIndex),
            shouldHighlight: false
          });
        }

        textParts.push({
          string: targetString.substr(highlightIndex, highlightString.length),
          shouldHighlight: true
        });
        targetString = targetString.substr(highlightIndex + highlightString.length);
      } else {
        textParts.push({
          string: targetString,
          shouldHighlight: false
        });
      }
    } while (highlightIndex !== -1);

    return textParts;
  }

  getArrayPartsByHighlight(targetString = '', highlightString = ['']) {
    const target = _.toLower(targetString);

    const indices = [];
    let index = 0;
    let lastWordLength = 0;

    for (let j = 0; j < highlightString.length; j++) {
      const word = _.toLower(highlightString[j]);

      const targetSuffix = target.substring(index + lastWordLength);
      const i = targetSuffix.indexOf(word);

      if (i >= 0) {
        const newIndex = index + lastWordLength + i;
        indices.push({
          start: index + lastWordLength + i,
          end: index + lastWordLength + i + word.length
        });
        index = newIndex;
        lastWordLength = word.length;
      } else {
        break;
      }
    }

    const parts = [];

    for (let k = 0; k < indices.length; k++) {
      if (k === 0 && indices[k].start !== 0) {
        parts.push({
          string: targetString.substring(0, indices[k].start),
          shouldHighlight: false
        });
      }

      parts.push({
        string: targetString.substring(indices[k].start, indices[k].end),
        shouldHighlight: true
      });

      if (k === indices.length - 1) {
        parts.push({
          string: targetString.substring(indices[k].end),
          shouldHighlight: false
        });
      } else {
        parts.push({
          string: targetString.substring(indices[k].end, indices[k + 1].start),
          shouldHighlight: false
        });
      }
    }

    return parts;
  }

  renderText(children) {
    const {
      highlightString,
      highlightStyle
    } = this.props;

    if (!_.isEmpty(highlightString)) {
      if (_.isArray(children)) {
        return _.map(children, child => {
          return this.renderText(child);
        });
      }

      if (_.isString(children)) {
        const textParts = highlightString && this.getPartsByHighlight(children, highlightString);
        return textParts && _.map(textParts, (text, index) => {
          return <RNText key={index} style={text.shouldHighlight ? [styles.highlight, highlightStyle] : styles.notHighlight}>
                {text.string}
              </RNText>;
        });
      }
    }

    return children;
  }

  render() {
    const {
      modifiers,
      style,
      center,
      uppercase,
      children,
      forwardedRef,
      ...others
    } = this.props;
    const color = this.props.color || modifiers.color;
    const {
      margins,
      typography,
      backgroundColor,
      flexStyle
    } = modifiers;
    const textStyle = [styles.container, typography, color && {
      color
    }, backgroundColor && {
      backgroundColor
    }, flexStyle, margins, center && styles.centered, uppercase && styles.uppercase, style];
    const TextContainer = this.TextContainer;
    return <TextContainer {...others} style={textStyle} ref={forwardedRef}>
        {this.renderText(children)}
      </TextContainer>;
  }

}

const styles = StyleSheet.create({
  container: {
    backgroundColor: 'transparent',
    textAlign: 'left'
  },
  centered: {
    textAlign: 'center'
  },
  uppercase: {
    textTransform: 'uppercase'
  },
  highlight: {
    color: Colors.grey30
  },
  notHighlight: {
    color: undefined
  }
});
export { Text }; // For tests

export default asBaseComponent(forwardRef(Text));
