}
export function extractPaddingValues(props) {
  const paddings = {};

  const paddingPropsKeys = _.chain(props).keys().filter(key => PADDING_KEY_PATTERN.test(key)).value();

  _.forEach(paddingPropsKeys, key => {
    if (props[key] === true) {
