import _pt from "prop-types";
import _ from 'lodash';
import React, { PureComponent } from 'react';
import { StyleSheet, PanResponder, AccessibilityInfo, Animated } from 'react-native';
import { Constants } from "../../helpers";
import { Colors } from "../../style";
import View from "../view";
import { extractAccessibilityProps } from "../../commons/modifiers";
const TRACK_SIZE = 6;
const THUMB_SIZE = 24;
const BORDER_WIDTH = 6;
const SHADOW_RADIUS = 4;
const DEFAULT_COLOR = Colors.grey50;
const ACTIVE_COLOR = Colors.violet30;
const INACTIVE_COLOR = Colors.grey60;
const defaultProps = {
  value: 0,
  minimumValue: 0,
  maximumValue: 1,
  step: 0
};
/**
 * @description: A Slider component
 * @example: https://github.com/wix/react-native-ui-lib/blob/master/demo/src/screens/componentScreens/SliderScreen.tsx
 * @gif: https://github.com/wix/react-native-ui-lib/blob/master/demo/showcase/Slider/Slider.gif?raw=true
 */

export default class Slider extends PureComponent {
  static propTypes = {
    /**
         * Initial value
         */
    value: _pt.number,

    /**
         * Minimum value
         */
    minimumValue: _pt.number,

    /**
         * Maximum value
         */
    maximumValue: _pt.number,

    /**
         * Step value of the slider. The value should be between 0 and (maximumValue - minimumValue)
         */
    step: _pt.number,

    /**
         * The color used for the track from minimum value to current value
         */
    minimumTrackTintColor: _pt.string,

    /**
         * The track color
         */
    maximumTrackTintColor: _pt.string,

    /**
         * Custom render instead of rendering the track
         */
    renderTrack: _pt.func,

    /**
         * Thumb color
         */
    thumbTintColor: _pt.string,

    /**
         * Callback for onValueChange
         */
    onValueChange: _pt.func,

    /**
         * Callback that notifies about slider seeking is started
         */
    onSeekStart: _pt.func,

    /**
         * Callback that notifies about slider seeking is finished
         */
    onSeekEnd: _pt.func,

    /**
         * If true the Slider will not change it's style on press
         */
    disableActiveStyling: _pt.bool,

    /**
         * If true the Slider will be disabled and will appear in disabled color
         */
    disabled: _pt.bool,

    /**
         * If true the component will have accessibility features enabled
         */
    accessible: _pt.bool,

    /**
       * The slider's test identifier
       */
    testID: _pt.string
  };
  static displayName = 'Slider';
  static defaultProps = defaultProps;
  thumb = undefined;
  _thumbStyles = {};
  minTrack = undefined;
  _minTrackStyles = {};
  _x = 0;
  _dx = 0;
  _thumbAnimationConstants = {
    duration: 100,
    defaultScaleFactor: 1.5
  };
  initialValue = this.getRoundedValue(this.props.value);
  lastValue = this.initialValue;
  initialThumbSize = {
    width: THUMB_SIZE,
    height: THUMB_SIZE
  };

  constructor(props) {
    super(props);
    this.state = {
      containerSize: {
        width: 0,
        height: 0
      },
      trackSize: {
        width: 0,
        height: 0
      },
      thumbSize: {
        width: 0,
        height: 0
      },
      thumbActiveAnimation: new Animated.Value(1),
      measureCompleted: false
    };
    this.checkProps(props);
    this._panResponder = PanResponder.create({
      onMoveShouldSetPanResponder: this.handleMoveShouldSetPanResponder,
      onPanResponderGrant: this.handlePanResponderGrant,
      onPanResponderMove: this.handlePanResponderMove,
      onPanResponderRelease: this.handlePanResponderEnd,
      onStartShouldSetPanResponder: () => true,
      onPanResponderEnd: () => true,
      onPanResponderTerminationRequest: () => false
    });
  }

  checkProps(props) {
    if (props.minimumValue >= props.maximumValue) {
      console.warn('Slider minimumValue must be lower than maximumValue');
    }

    if (props.value < props.minimumValue || props.value > props.maximumValue) {
      console.warn('Slider value is not in range');
    }
  }

  getAccessibilityProps() {
    const {
      disabled
    } = this.props;
    return {
      accessibilityLabel: 'Slider',
      accessible: true,
      accessibilityRole: 'adjustable',
      accessibilityStates: disabled ? ['disabled'] : [],
      accessibilityActions: [{
        name: 'increment',
        label: 'increment'
      }, {
        name: 'decrement',
        label: 'decrement'
      }],
      ...extractAccessibilityProps(this.props)
