      friction,
      overshootFriction
    } = this.props;
    const {
      dragX,
      rowTranslation,
      leftWidth = 0,
      rowWidth = 0
    } = this.state;
    const {
      rightOffset = rowWidth
    } = this.state;
    const rightWidth = Math.max(0, rowWidth - rightOffset);
